);\n\n					$data[\'information_seo_url\'][$store_id][$language_id] = $this->load->controller(\'extension/module/seo_url_generator/generateSeoUrl\', $sug_data);\n					//$this->cache->delete(\'seo_pro\');\n					continue;\n				}\n\n				if (!$keyword_old && $data[\'information_seo_url\'][$store_id][$language_id]) {\n					// Снова все понятно: старого ЧПУ в базе нет, соглашаемся с ЧПУ из формы\n					// Q?\n					// А транлитировать этот ЧПУ надо или нет?..\n\n					//$this->cache->delete(\'seo_pro\');\n					continue;\n				}\n\n				if ($keyword_old && !$data[\'information_seo_url\'][$store_id][$language_id]) {\n					// Просто используем существующий ЧПУ из базы\n					$data[\'information_seo_url\'][$store_id][$language_id] = $keyword_old;\n				}\n\n				if ($keyword_old && $data[\'information_seo_url\'][$store_id][$language_id] && $keyword_old != $data[\'information_seo_url\'][$store_id][$language_id]) {\n					// Запускаем механизм редиректов, только если на фронте не работает\n					if (!$sug_front_ok) {\n						$sug_set_backend_autoredirects = true;\n					}\n\n					$keyword_new = $data[\'information_seo_url\'][$store_id][$language_id];\n\n					// Q?\n					// А транлитировать этот ЧПУ надо или нет?..\n					// Минуем актуализацию, итак понятно, что $keyword_old != $data[\'information_seo_url\'][$store_id][$language_id]\n					goto sug_edit_end;\n				}\n        \n        if ($keyword_old && $data[\'information_seo_url\'][$store_id][$language_id] && $keyword_old == $data[\'information_seo_url\'][$store_id][$language_id] && $sug_data[\'setting\'][\'rewrite_on_save\']) {\n					// Актуализация по данным сущности - название, другая формула\n					// ставить ли редирект, будет понятно лишь после сравнения старого и нового ЧПУ\n\n					$keyword_new = $this->load->controller(\'extension/module/seo_url_generator/generateSeoUrl\', $sug_data);\n				}\n\n				// Make unique\n				if ($keyword_new && !$this->model_extension_module_seo_url_generator->isUnique($keyword_new, $sug_data[\'primary_key\'], $sug_data[\'essence_id\'], $store_id)) {\n					$keyword_new = $this->model_extension_module_seo_url_generator->makeUniqueUrl($keyword_new, $store_id);\n				}\n\n				// Актуализация по разделителю\n				if ($keyword_new && $sug_data[\'setting\'][\'rewrite_on_save\']) {\n					$sug_log->write(4, \'model/information.php :: editInformation() : Actualization by delimeter BEGIN\');\n\n					if (\'donot\' != $sug_data[\'setting\'][\'change_delimiter_char\']) {\n						// Compare without delimiters\n						$keyword_old_without_delimiters = preg_replace(array(\'|_+|\', \'|-+|\'), array(\'\', \'\'), $keyword_old);\n						$keyword_new_without_delimiters = preg_replace(array(\'|_+|\', \'|-+|\'), array(\'\', \'\'), $keyword_new);\n\n						$sug_log->write(3, $keyword_old_without_delimiters, \'generateSeoUrl() : $keyword_old_without_delimiters\');\n						$sug_log->write(3, $keyword_new_without_delimiters, \'generateSeoUrl() : $keyword_new_without_delimiters\');\n\n						if ($keyword_old_without_delimiters != $keyword_new_without_delimiters) {\n							$sug_set_backend_autoredirects = true;\n							$data[\'information_seo_url\'][$store_id][$language_id] = $keyword_new;\n						}\n					} else {\n						// Compare with delimiters\n						if ($keyword_old != $keyword_new) {\n							$sug_set_backend_autoredirects = true;\n							$data[\'information_seo_url\'][$store_id][$language_id] = $keyword_new;\n						}\n					}\n				}\n\n				// Write Redirect\n				sug_edit_end:\n\n				$sug_log->write(4, \'model/information.php :: editInformation() : sug_redirects BEGIN\');\n\n				$sug_log->write(4, $sug_set_backend_autoredirects, \'model/information.php :: editInformation() : $sug_set_backend_autoredirects\');\n\n				$sug_log->write(4, $keyword_new, \'model/information.php :: editInformation() : $keyword_new\');\n\n				// Удаляем все существующие редиректы из базы для данной сущности - на фронте предупреждение было выдано, чтобы не убирали из формы\n				$this->db->query(\"DELETE FROM \" . DB_PREFIX . \"seo_url_generator_redirects WHERE query = \'information_id=\" . (int)$information_id . \"\' AND `store_id` = \'\" . (int)$store_id . \"\' AND `language_id` = \'\" . (int)$language_id . \"\'\");\n\n				// Обрабатываем редиректы из формы - все управление редриректами у пользователя на фронте!\n				if (isset($data[\'seo_url_generator_redirects\'][$store_id][$language_id]) && count($data[\'seo_url_generator_redirects\'][$store_id][$language_id]) > 0) {\n					$sug_log->write(4, $data[\'seo_url_generator_redirects\'][$store_id][$language_id], \'model/information.php :: editInformation() : $data[\"seo_url_generator_redirects\"][$store_id][$language_id]\');\n\n					$data[\'seo_url_generator_redirects\'][$store_id][$language_id] = array_unique($data[\'seo_url_generator_redirects\'][$store_id][$language_id]); // на всякий случай...\n\n					foreach ($data[\'seo_url_generator_redirects\'][$store_id][$language_id] as $redirect) {\n						$this->db->query(\"INSERT INTO \" . DB_PREFIX . \"seo_url_generator_redirects SET store_id = \'\" . (int)$store_id . \"\', language_id = \'\" . (int)$language_id . \"\', seo_url_old = \'\" . $this->db->escape($redirect) . \"\', seo_url_actual = \'\" . $this->db->escape($data[\'information_seo_url\'][$store_id][$language_id]) . \"\', query = \'information_id=\" . (int)$information_id . \"\'\");\n							}\n						}\n\n				if ($sug_set_backend_autoredirects) {\n					$sug_log->write(3, \'model/information.php :: editInformation() : Autoredirect was created on backend\');\n\n					// setRedirect() кроме того, что просто записывет текущий редирект, также обновляет новый ЧПУ для всех старый редиректов\n					$this->model_extension_module_seo_url_generator->setRedirect($keyword_new, $keyword_old, $sug_data[\'primary_key\'], $sug_data[\'essence_id\'], $store_id, $language_id);\n				}\n			}\n		}\n		/* SEO URL Generator . end\n		------------------------------------------------------------------------- */\n]]></add>\n    </operation>\n	</file>\n	<file path=\"admin/view/template/catalog/information_form.twig\">\n    <operation>\n      <search><![CDATA[\n        <input type=\"text\" name=\"information_seo_url[{{ store.store_id }}][{{ language.language_id }}]\" value=\"{% if information_seo_url[store.store_id][language.language_id] %}{{ information_seo_url[store.store_id][language.language_id] }}{% endif %}\" placeholder=\"{{ entry_keyword }}\" class=\"form-control\" />\n			]]></search>\n      <add position=\"replace\"><![CDATA[<input type=\"text\" name=\"information_seo_url[{{ store.store_id }}][{{ language.language_id }}]\" value=\"{% if information_seo_url[store.store_id][language.language_id] %}{{ information_seo_url[store.store_id][language.language_id] }}{% endif %}\" placeholder=\"{{ entry_keyword }}\" class=\"form-control\" id=\"input-seo-url-{{ store.store_id }}-{{ language.language_id }}\" data-seo-url-was=\"{% if information_seo_url[store.store_id][language.language_id] %}{{ information_seo_url[store.store_id][language.language_id] }}{% endif %}\"/>{% if information_id %}<span class=\"input-group-btn\"><button id=\"generateUrlOnEdit-{{ store.store_id }}-{{ language.language_id }}\" class=\"generateUrlOnEdit btn btn-success\" data-store-id=\"{{ store.store_id }}\" data-language-id=\"{{ language.language_id }}\"><i class=\"fa fa-refresh\"></i> {{ sug_button_generate }}</button></span>{% endif %}]]></add>\n    </operation>\n		<operation>\n			<search><![CDATA[\n				{{ footer }}\n			]]></search>\n			<add position=\"before\"><![CDATA[\n<!-- SEO URL Generator . Begin\n============================================================================ -->\n{% if (module_seo_url_generator_status) %}\n<script type=\"text/javascript\">\n  $(\'#tab-seo .text-left .input-group\').css(\'margin-bottom\', \'10px\');\n\n  $.each($(\'#tab-seo .text-left\'), function () {      \n    $(this).children(\'.input-group:last\').css(\'margin-bottom\', \'initial\');\n  });\n\n  {% if (not information_id) %}\n    \n    {% for language in languages %}\n    $(\'#input-title\' + {{ language.language_id }}).change(function(){ generateUrlOnAdd(); });\n    {% endfor %}\n\n    function generateUrlOnAdd() {\n      let a_lang_data = {\n        name: {}\n      };\n      \n      {% for language in languages %}\n      a_lang_data[\'name\'][{{ language.language_id }}] = $(\'#input-title\' + {{ language.language_id }}).val();\n      {% endfor %}\n            \n      data = {\n        a_lang_data     : a_lang_data,\n        primary_key     : \'information_id\',\n        essence         : \'information\',\n        essence_id      : \'\'\n      };\n\n      // is different from edit\n      $.ajax({\n        url: \'index.php?route=extension/module/seo_url_generator/generateSeoUrlByAjax4All&user_token={{ user_token }}\',\n        dataType: \'json\',\n        data: data,\n        method : \'POST\',\n        beforeSend: function() {},\n        success: function(response, textStatus, jqXHR) {\n          // success ajax query\n          if(response.result != \'error\') {\n            setTimeout(function() {      \n              $.each(response.result, function (store_id, store) {\n                $.each(store, function (language_id, value) {\n                  if (\'\' !== value) {\n                    $(\'input[name=\"information_seo_url[\' + store_id +\'][\' + language_id + \']\"]\').val(value);\n                  }\n                });\n              });\n            }, 100);\n          }\n        },\n        error: function(jqXHR, textStatus, errorThrown) {\n          // Error ajax query\n          console.log(\'AJAX query Error: \' + textStatus);\n        },\n        complete: function() {},\n      });\n    }\n\n  {% else %}   \n\n    var storeId = false;      \n    var languageId = false;\n    \n    // enque redirects .generateUrlOnEdit firstly\n    var url_redirects_rows = {};\n    \n    {% for store in stores %}\n    url_redirects_rows[{{ store.store_id }}] = {};\n    \n    {% for language in languages %}\n    url_redirects_rows[{{ store.store_id }}][{{ language.language_id }}] = 0;\n    \n    {% endfor %}\n    {% endfor %}\n    \n    {% for store in stores %}\n    {% for language in languages %}\n   \n      var html = \'\';\n      html += \'<div class=\"form-group\">\';\n      html += \'<label class=\"col-sm-2 control-label\">{{ sug_text_redirects }}</label>\';\n      html += \'<!-- information-redirects . begin -->\';\n      html += \'<div class=\"col-sm-10\">\';\n      html += \'<div class=\"alert alert-info alert-sm\">{{ sug_help_redirects }}</div>\';\n      html += \'<div id=\"seo_url_generator_redirects-{{ store.store_id }}-{{ language.language_id }}\">\';\n      \n      {% for key, redirect in redirects[store[\'store_id\']][language[\'language_id\']] %}\n      {% if redirects[store[\'store_id\']][language[\'language_id\']][key] %}\n      html += \'<div id=\"redirect-row-{{ store.store_id }}-{{ language.language_id }}-\' + url_redirects_rows[{{ store.store_id }}][{{ language.language_id }}] + \'\" class=\"row redirect-row\" style=\"margin: 10px 0;\"><input type=\"text\" name=\"seo_url_generator_redirects[{{ store.store_id }}][{{ language.language_id }}][{{ key }}]\" value=\"{{ redirects[store[\'store_id\']][language[\'language_id\']][key] }}\" class=\"form-control input-sm col-xs-10\" style=\"width: 100%; width: calc(100% - 120px); {% if error_seo_url_generator_redirects[store[\'store_id\']][language[\'language_id\']][key] is defined %}border-color: red;{% endif %}\"/><div class=\"col-xs-1\"><i class=\"pull-left fa fa-close text-danger deleteRedirect\" style=\"cursor: pointer;\" data-toggle=\"tooltip\" title=\"{{ sug_button_delete_redirect }}\"></i></div></div>\';\n      url_redirects_rows[{{ store.store_id }}][{{ language.language_id }}]++;\n      {% endif %}\n      {% endfor %}	\n      html += \'</div>\';\n      html += \'<!-- /information-redirects . end -->\';\n      html += \'<button id=\"addNewRedirect-{{ store.store_id }}-{{ language.language_id }}\" class=\"addNewRedirect btn btn-warning btn-sm\" data-store-id=\"{{ store.store_id }}\" data-language-id=\"{{ language.language_id }}\"><i class=\"fa fa-plus\"></i> {{ sug_button_add_redirect }}</button>\';\n      html += \'</div>\';\n      html += \'</div>\';\n      \n      <!-- Front works properly mark -->\n      html += \'<input type=\"hidden\" name=\"seo_url_generator_front_works\" />\';\n      \n      //console.log(\'#generateUrlOnEdit-\' + {{ store.store_id }} + \'-\' + {{ language.language_id }});\n      //console.debug(html);\n      \n      $(\'#generateUrlOnEdit-\' + {{ store.store_id }} + \'-\' + {{ language.language_id }}).parent().parent().after(html);\n            \n    {% endfor %}\n    {% endfor %}\n    \n    $(\'.addNewRedirect\').click(function(e) {\n      e.preventDefault();\n      \n      var store_id = $(this).data(\'store-id\');\n      var language_id =  $(this).data(\'language-id\');\n      \n      console.log(\'$(this).data(\"store-id\") : \' + $(this).data(\'store-id\'));\n      console.log(\'$(this).data(\"language-id\") : \' + $(this).data(\'language-id\'));\n      console.log(\'#seo_url_generator_redirects-\" + store_id + \"-\" + language_id : #seo_url_generator_redirects-\' + store_id + \'-\' + language_id);\n\n      $(\'#seo_url_generator_redirects-\' + store_id + \'-\' + language_id).append(\'<div id=\"redirect-row-\' + \'-\' + store_id + \'-\' + language_id + \'-\' + url_redirects_rows[store_id][language_id] + \'\" class=\"row redirect-row recent\" style=\"margin: 10px 0;\"><input type=\"text\" name=\"seo_url_generator_redirects[\' + store_id + \'][\' + language_id + \'][]\" value=\"\" class=\"form-control input-sm col-xs-10\" style=\"width: 100%; width: calc(100% - 120px);\" /><div class=\"col-xs-1\"><i class=\"pull-left fa fa-close text-danger deleteRedirect\" style=\"cursor: pointer;\" data-toggle=\"tooltip\" title=\"{{ sug_button_delete_redirect }}\"></i></div></div>\');\n\n      url_redirects_rows[store_id][language_id]++;\n    });\n\n    $(\'body\').