ore_id => $store) {\n			foreach ($store as $language_id => $url) {				\n\n				# SEO URL PROCESSING\n\n				$sug_data = array(\n					\'name\'            => $data[\'article_description\'][$language_id][\'name\'],\n					\'primary_key\'     => \'article_id\',\n					\'essence\'         => \'article\',\n					\'essence_id\'      => $this->request->get[\'article_id\'],\n				);\n\n				$sug_data[\'setting\'] = array(\n					\'translit_function\'     =>$data[\'store_setting\'][$store_id][\'translit_function\'][$language_id],\n					\'formula\'               =>\'\',\n					\'delimiter_char\'        =>$data[\'store_setting\'][$store_id][\'delimiter_char\'][$language_id],\n					\'change_delimiter_char\' =>$data[\'store_setting\'][$store_id][\'change_delimiter_char\'][$language_id],\n					\'rewrite_on_save\'       =>$data[\'store_setting\'][$store_id][\'rewrite_on_save\'][$language_id],\n					\'custom_replace_from\'   =>$data[\'store_setting\'][$store_id][\'custom_replace_from\'][$language_id],\n					\'custom_replace_to\'     =>$data[\'store_setting\'][$store_id][\'custom_replace_to\'][$language_id],\n				);\n        \n        $sug_data[\'store_id\'] = $store_id;\n\n				$data[\'article_seo_url\'][$store_id][$language_id] = trim($data[\'article_seo_url\'][$store_id][$language_id]);\n\n				$sug_log->write(2, $data[\'article_seo_url\'][$store_id][$language_id], \'model/blog/article.php :: editArticle() : $data[\"article_seo_url\"][$store_id][$language_id]\');\n\n				$sug_set_backend_autoredirects = false;\n\n				$keyword_new = false;\n\n				$keyword_old = count($keywords_old) > 0 && isset($keywords_old[$store_id][$language_id]) ? $keywords_old[$store_id][$language_id] : \'\';\n\n				$sug_log->write(4, $keyword_old, \'model/blog/article.php :: editArticle() : $keyword_old\');\n\n				$sug_log->write(4, $sug_data, \'model/blog/article.php :: editArticle() : $sug_data\');\n\n				if (!$keyword_old && !$data[\'article_seo_url\'][$store_id][$language_id]) {\n					// Все понятно: старого ЧПУ нет, просто генерим новый. Редиректы не нужны.\n					$sug_log->write(4, $keyword_old, \'model/blog/article.php :: editArticle() : EMPTY $keyword_old & $data[\"article_seo_url\"][$store_id][$language_id]\');\n\n					$data[\'article_seo_url\'][$store_id][$language_id] = $this->load->controller(\'extension/module/seo_url_generator/generateSeoUrl\', $sug_data);\n					//$this->cache->delete(\'seo_pro\');\n					continue;\n				}\n\n				if (!$keyword_old && $data[\'article_seo_url\'][$store_id][$language_id]) {\n					// Снова все понятно: старого ЧПУ в базе нет, соглашаемся с ЧПУ из формы\n					// Q?\n					// А транлитировать этот ЧПУ надо или нет?..\n\n					//$this->cache->delete(\'seo_pro\');\n					continue;\n				}\n\n				if ($keyword_old && !$data[\'article_seo_url\'][$store_id][$language_id]) {\n					// Просто используем существующий ЧПУ из базы\n					$data[\'article_seo_url\'][$store_id][$language_id] = $keyword_old;\n				}\n\n				if ($keyword_old && $data[\'article_seo_url\'][$store_id][$language_id] && $keyword_old != $data[\'article_seo_url\'][$store_id][$language_id]) {\n					// Запускаем механизм редиректов, только если на фронте не работает\n					if (!$sug_front_ok) {\n						$sug_set_backend_autoredirects = true;\n					}\n\n					$keyword_new = $data[\'article_seo_url\'][$store_id][$language_id];\n\n					// Q?\n					// А транлитировать этот ЧПУ надо или нет?..\n					// Минуем актуализацию, итак понятно, что $keyword_old != $data[\'article_seo_url\'][$store_id][$language_id]\n					goto sug_edit_end;\n				}\n        \n        if ($keyword_old && $data[\'article_seo_url\'][$store_id][$language_id] && $keyword_old == $data[\'article_seo_url\'][$store_id][$language_id] && $sug_data[\'setting\'][\'rewrite_on_save\']) {\n					// Актуализация по данным сущности - название, другая формула\n					// ставить ли редирект, будет понятно лишь после сравнения старого и нового ЧПУ\n\n					$keyword_new = $this->load->controller(\'extension/module/seo_url_generator/generateSeoUrl\', $sug_data);\n				}\n\n				// Make unique\n				if ($keyword_new && !$this->model_extension_module_seo_url_generator->isUnique($keyword_new, $sug_data[\'primary_key\'], $sug_data[\'essence_id\'], $store_id)) {\n					$keyword_new = $this->model_extension_module_seo_url_generator->makeUniqueUrl($keyword_new, $store_id);\n				}\n\n				// Актуализация по разделителю\n				if ($keyword_new && $sug_data[\'setting\'][\'rewrite_on_save\']) {\n					$sug_log->write(4, \'model/blog/article.php :: editArticle() : Actualization by delimeter BEGIN\');\n\n					if (\'donot\' != $sug_data[\'setting\'][\'change_delimiter_char\']) {\n						// Compare without delimiters\n						$keyword_old_without_delimiters = preg_replace(array(\'|_+|\', \'|-+|\'), array(\'\', \'\'), $keyword_old);\n						$keyword_new_without_delimiters = preg_replace(array(\'|_+|\', \'|-+|\'), array(\'\', \'\'), $keyword_new);\n\n						$sug_log->write(3, $keyword_old_without_delimiters, \'generateSeoUrl() : $keyword_old_without_delimiters\');\n						$sug_log->write(3, $keyword_new_without_delimiters, \'generateSeoUrl() : $keyword_new_without_delimiters\');\n\n						if ($keyword_old_without_delimiters != $keyword_new_without_delimiters) {\n							$sug_set_backend_autoredirects = true;\n							$data[\'article_seo_url\'][$store_id][$language_id] = $keyword_new;\n						}\n					} else {\n						// Compare with delimiters\n						if ($keyword_old != $keyword_new) {\n							$sug_set_backend_autoredirects = true;\n							$data[\'article_seo_url\'][$store_id][$language_id] = $keyword_new;\n						}\n					}\n				}\n\n				// Write Redirect\n				sug_edit_end:\n\n				$sug_log->write(4, \'model/blog/article.php :: editArticle() : sug_redirects BEGIN\');\n\n				$sug_log->write(4, $sug_set_backend_autoredirects, \'model/blog/article.php :: editArticle() : $sug_set_backend_autoredirects\');\n\n				$sug_log->write(4, $keyword_new, \'model/blog/article.php :: editArticle() : $keyword_new\');\n\n				// Удаляем все существующие редиректы из базы для данной сущности - на фронте предупреждение было выдано, чтобы не убирали из формы\n				$this->db->query(\"DELETE FROM \" . DB_PREFIX . \"seo_url_generator_redirects WHERE query = \'article_id=\" . (int)$article_id . \"\' AND `store_id` = \'\" . (int)$store_id . \"\' AND `language_id` = \'\" . (int)$language_id . \"\'\");\n\n				// Обрабатываем редиректы из формы - все управление редриректами у пользователя на фронте!\n				if (isset($data[\'seo_url_generator_redirects\'][$store_id][$language_id]) && count($data[\'seo_url_generator_redirects\'][$store_id][$language_id]) > 0) {\n					$sug_log->write(4, $data[\'seo_url_generator_redirects\'][$store_id][$language_id], \'model/blog/article.php :: editArticle() : $data[\"seo_url_generator_redirects\"][$store_id][$language_id]\');\n\n					$data[\'seo_url_generator_redirects\'][$store_id][$language_id] = array_unique($data[\'seo_url_generator_redirects\'][$store_id][$language_id]); // на всякий случай...\n\n					foreach ($data[\'seo_url_generator_redirects\'][$store_id][$language_id] as $redirect) {\n						$this->db->query(\"INSERT INTO \" . DB_PREFIX . \"seo_url_generator_redirects SET store_id = \'\" . (int)$store_id . \"\', language_id = \'\" . (int)$language_id . \"\', seo_url_old = \'\" . $this->db->escape($redirect) . \"\', seo_url_actual = \'\" . $this->db->escape($data[\'article_seo_url\'][$store_id][$language_id]) . \"\', query = \'article_id=\" . (int)$article_id . \"\'\");\n							}\n						}\n\n				if ($sug_set_backend_autoredirects) {\n					$sug_log->write(3, \'model/blog/article.php :: editArticle() : Autoredirect was created on backend\');\n\n					// setRedirect() кроме того, что просто записывет текущий редирект, также обновляет новый ЧПУ для всех старый редиректов\n					$this->model_extension_module_seo_url_generator->setRedirect($keyword_new, $keyword_old, $sug_data[\'primary_key\'], $sug_data[\'essence_id\'], $store_id, $language_id);\n				}\n			}\n		}\n		/* SEO URL Generator . end\n		------------------------------------------------------------------------- */\n]]></add>\n    </operation>\n	</file>\n	<file path=\"admin/view/template/blog/article_form.twig\">\n    <operation>\n      <search><![CDATA[\n        <input type=\"text\" name=\"article_seo_url[{{ store.store_id }}][{{ language.language_id }}]\" value=\"{% if article_seo_url[store.store_id][language.language_id] %}{{ article_seo_url[store.store_id][language.language_id] }}{% endif %}\" placeholder=\"{{ entry_keyword }}\" class=\"form-control\" />\n			]]></search>\n      <add position=\"replace\"><![CDATA[<input type=\"text\" name=\"article_seo_url[{{ store.store_id }}][{{ language.language_id }}]\" value=\"{% if article_seo_url[store.store_id][language.language_id] %}{{ article_seo_url[store.store_id][language.language_id] }}{% endif %}\" placeholder=\"{{ entry_keyword }}\" class=\"form-control\" id=\"input-seo-url-{{ store.store_id }}-{{ language.language_id }}\" data-seo-url-was=\"{% if article_seo_url[store.store_id][language.language_id] %}{{ article_seo_url[store.store_id][language.language_id] }}{% endif %}\"/>{% if article_id %}<span class=\"input-group-btn\"><button id=\"generateUrlOnEdit-{{ store.store_id }}-{{ language.language_id }}\" class=\"generateUrlOnEdit btn btn-success\" data-store-id=\"{{ store.store_id }}\" data-language-id=\"{{ language.language_id }}\"><i class=\"fa fa-refresh\"></i> {{ sug_button_generate }}</button></span>{% endif %}]]></add>\n    </operation>\n		<operation>\n			<search><![CDATA[\n				{{ footer }}\n			]]></search>\n			<add position=\"before\"><![CDATA[\n<!-- SEO URL Generator . Begin\n============================================================================ -->\n{% if (module_seo_url_generator_status is defined and module_seo_url_generator_status) %}\n<script type=\"text/javascript\">\n  $(\'#tab-seo .text-left .input-group\').css(\'margin-bottom\', \'10px\');\n\n  $.each($(\'#tab-seo .text-left\'), function () {      \n    $(this).children(\'.input-group:last\').css(\'margin-bottom\', \'initial\');\n  });\n\n  {% if (not article_id) %}\n    \n    {% for language in languages %}\n    $(\'#input-name\' + {{ language.language_id }}).change(function(){ generateUrlOnAdd(); });\n    {% endfor %}\n\n    function generateUrlOnAdd() {\n      let a_lang_data = {\n        name: {}\n      };\n      \n      {% for language in languages %}\n      a_lang_data[\'name\'][{{ language.language_id }}] = $(\'#input-name\' + {{ language.language_id }}).val();\n      {% endfor %}\n            \n      data = {\n        a_lang_data     : a_lang_data,\n        primary_key     : \'article_id\',\n        essence         : \'article\',\n        essence_id      : \'\'\n      };\n\n      // is different from edit\n      $.ajax({\n        url: \'index.php?route=extension/module/seo_url_generator/generateSeoUrlByAjax4All&user_token={{ user_token }}\',\n        dataType: \'json\',\n        data: data,\n        method : \'POST\',\n        beforeSend: function() {},\n        success: function(response, textStatus, jqXHR) {\n          // success ajax query\n          if(response.result != \'error\') {\n            setTimeout(function() {      \n              $.each(response.result, function (store_id, store) {\n                $.each(store, function (language_id, value) {\n                  if (\'\' !== value) {\n                    $(\'input[name=\"article_seo_url[\' + store_id +\'][\' + language_id + \']\"]\').val(value);\n                  }\n                });\n              });\n            }, 100);\n          }\n        },\n        error: function(jqXHR, textStatus, errorThrown) {\n          // Error ajax query\n          console.log(\'AJAX query Error: \' + textStatus);\n        },\n        complete: function() {},\n      });\n    }\n\n  {% else %}   \n\n    var storeId = false;      \n    var languageId = false;\n    \n    // enque redirects .generateUrlOnEdit firstly\n    var url_redirects_rows = {};\n    \n    {% for store in stores %}\n    url_redirects_rows[{{ store.store_id }}] = {};\n    \n    {% for language in languages %}\n    url_redirects_rows[{{ store.store_id }}][{{ language.language_id }}] = 0;\n    \n    {% endfor %}\n    {% endfor %}\n    \n    {% for store in stores %}\n    {% for language in languages %}\n   \n      var html = \'\';\n      html += \'<div class=\"form-group\">\';\n      html += \'<label class=\"col-sm-2 control-label\">{{ sug_text_redirects }}</label>\';\n      html += \'<!-- article-redirects . begin -->\';\n      html += \'<div class=\"col-sm-10\">\';\n      html += \'<div class=\"alert alert-info alert-sm\">{{ sug_help_redirects }}</div>\';\n      html += \'<div id=\"seo_url_generator_redirects-{{ store.store_id }}-{{ language.language_id }}\">\';\n      \n      {% for key, redirect in redirects[store[\'store_id\']][language[\'language_id\']] %}\n      {% if redirects[store[\'store_id\']][language[\'language_id\']][key] %}\n      html += \'<div id=\"redirect-row-{{ store.store_id }}-{{ language.language_id }}-\' + url_redirects_rows[{{ store.store_id }}][{{ language.language_id }}] + \'\" class=\"row redirect-row\" style=\"margin: 10px 0;\"><input type=\"text\" name=\"seo_url_generator_redirects[{{ store.store_id }}][{{ language.language_id }}][{{ key }}]\" value=\"{{ redirects[store[\'store_id\']][language[\'language_id\']][key] }}\" class=\"form-control input-sm col-xs-10\" style=\"width: 100%; width: calc(100% - 120px); {% if error_seo_url_generator_redirects[store[\'store_id\']][language[\'language_id\']][key] is defined %}border-color: red;{% endif %}\"/><div class=\"col-xs-1\"><i class=\"pull-left fa fa-close text-danger deleteRedirect\" style=\"cursor: pointer;\" data-toggle=\"tooltip\" title=\"{{ sug_button_delete_redirect }}\"></i></div></div>\';\n      url_redirects_rows[{{ store.store_id }}][{{ language.language_id }}]++;\n      {% endif %}\n      {% endfor %}	\n      html += \'</div>\';\n      html += \'<!-- /article-redirects . end -->\';\n      html += \'<button id=\"addNewRedirect-{{ store.store_id }}-{{ language.language_id }}\" class=\"addNewRedirect btn btn-warning btn-sm\" data-store-id=\"{{ store.store_id }}\" data-language-id=\"{{ language.language_id }}\"><i class=\"fa fa-plus\"></i> {{ sug_button_add_redirect }}</button>\';\n      html += \'</div>\';\n      html += \'</div>\';\n      \n      <!-- Front works properly mark -->\n      html += \'<input type=\"hidden\" name=\"seo_url_generator_front_works\" />\';\n      \n      //console.log(\'#generateUrlOnEdit-\' + {{ store.store_id }} + \'-\' + {{ language.language_id }});\n      //console.debug(html);\n      \n      $(\'#generateUrlOnEdit-\' + {{ store.store_id }} + \'-\' + {{ language.language_id }}).parent().parent().after(html);\n            \n    {% endfor %}\n    {% endfor %}\n    \n    $(\'.addNewRedirect\').click(function(e) {\n      e.preventDefault();\n      \n      var store_id = $(this).data(\'store-id\');\n      var language_id =  $(this).data(\'language-id\');\n      \n      console.log(\'$(this).data(\"store-id\") : \' + $(this).data(\'store-id\'));\n      console.log(\'$(this).data(\"language-id\") : \' + $(this).data(\'language-id\'));\n      console.log(\'#seo_url_generator_redirects-\" + store_id + \"-\" + language_id : #seo_url_generator_redirects-\' + store_id + \'-\' + language_id);\n\n      $(\'#seo_url_generator_redirects-\' + store_id + \'-\' + language_id).append(\'<div id=\"redirect-row-\' + \'-\' + store_id + \'-\' + language_id + \'-\' + url_redirects_rows[store_id][language_id] + \'\" class=\"row redirect-row recent\" style=\"margin: 10px 0;\"><input type=\"text\" name=\"seo_url_generator_redirects[\' + store_id + \'][\' + language_id + \'][]\" value=\"\" class=\"form-control input-sm col-xs-10\" style=\"width: 100%; width: calc(100% - 120px);\" /><div class=\"col-xs-1\"><i class=\"pull-left fa fa-close text-danger deleteRedirect\" style=\"cursor: pointer;\" data-toggle=\"tooltip\" title=\"{{ sug_button_delete_redirect }}\"></i></div></div>\');\n\n      url_redirects_rows[store_id][language_id]++;\n    });\n\n    $(\'body\').on(\'click\', \'.deleteRedirect\', function(e) {\n      e.preventDefault();\n      $(\'#sug-btn-confirm\').attr(\'data-target\', \'#\' + $(this).parent().parent(\'.redirect-row\').attr(\'id\'));		\n      $(\'#sug-confirm\').modal(\'toggle\');    \n    });\n\n    $(\'body\').on(\'click\', \'#sug-btn-confirm\', function(e) {\n      e.preventDefault();		\n      $($(this).attr(\'data-target\')).remove();		\n      $(\'#sug-confirm\').modal(\'toggle\');\n    });\n    \n    \n    // SEO URL Generation by button click\n  \n    thisButton = false;\n    \n    $(\'.generateUrlOnEdit\').click(function(e) {\n      e.preventDefault();\n      \n      storeId = $(this).data(\'store-id\');      \n      languageId = $(this).data(\'language-id\');\n      thisButton = $(this);\n            \n      data = {\n        store_id        : storeId,\n        language_id     : languageId,\n        name            : $(\'#input-name\' + languageId).val(),\n        primary_key     : \'article_id\',\n        essence         : \'article\',\n        essence_id      : {{ article_id }}\n      };     \n\n      getSeoUrl(data);\n    });\n    \n    // is different from add\n    function getSeoUrl(data) {\n      $.ajax({\n        url: \'index.php?route=extension/module/seo_url_generator/generateSeoUrlByAjax4One&user_token={{ user_token }}\',\n        dataType: \'json\',\n        data: data,\n        method : \'POST\',\n        beforeSend: function() {\n          thisButton.css(\'opacity\', \'0.5\');\n        },\n        success: function(response, textStatus, jqXHR) {\n          // success ajax query\n          if(\'\' != response.result && \'error\' != response.result) {            \n            console.log(\'$(\"#input-seo-url-\" + storeId + \"-\" + languageId).val() : \' + $(\'#input-seo-url-\' + storeId + \'-\' + languageId).val());\n            console.log(\'response.result : \' + response.result);\n            \n            if (\'\' != $(\'#input-seo-url-\' + storeId + \'-\' + languageId).val().trim() && $(\'#input-seo-url-\' + storeId + \'-\' + languageId).val() != response.result) {    \n              $(\'#seo_url_generator_redirects-\' + storeId + \'-\' + languageId).append(\'<div id=\"redirect-row-\' + \'-\' + storeId + \'-\' + languageId + \'-\' + url_redirects_rows[storeId][languageId] + \'\" class=\"row redirect-row recent\" style=\"margin: 10px 0;\"><input type=\"text\" name=\"seo_url_generator_redirects[\' + storeId + \'][\' + languageId + \'][]\" value=\"\' + $(\'#input-seo-url-\' + storeId + \'-\' + languageId).data(\'seo-url-was\') + \'\" class=\"form-control input-sm col-xs-10\" style=\"width: 100%; width: calc(100% - 120px);\" /><div class=\"col-xs-1\"><i class=\"pull-left fa fa-close text-danger deleteRedirect\" style=\"cursor: pointer;\" data-toggle=\"tooltip\" title=\"{{ sug_button_delete_redirect }}\"></i></div></div>\');\n\n              url_redirects_rows[storeId][languageId]++;\n            }\n            \n            setTimeout(function() {\n              $(\'#input-seo-url-\' + storeId + \'-\' + languageId).val(response.result);\n            }, 100);\n          }\n        },\n        error: function(jqXHR, textStatus, errorThrown) {\n          // Error ajax query\n          console.log(\'AJAX query Error: \' + textStatus);\n        },\n        complete: function() {\n          thisButton.css(\'opacity\', \'1\');\n        },\n      });\n    }\n\n  {% endif %}  \n\n  // Prevent incorrect SEO URL!\n	var inputBorderColorInitial = false;\n	\n	$(\'body\').on(\'change\', \'.redirect-row input\', function(e) {\n		if (!inputBorderColorInitial) {\n			inputBorderColorInitial = $(this).css(\'border-color\');\n		}\n		\n		// reset previous errors\n		$(this).css(\'border-color\', inputBorderColorInitial);\n		\n		$(\'#sug-error-body\').html(\'\');\n		\n		let hasError = false;\n		\n		// Check inputed data		\n		if ($(this).val().trim() == \'\') {\n			hasError = true;\n			$(\'#sug-error-body\').append(\'<p class=\"text-danger\"><b>{{ sug_redirects_error_empty }}</b></p>\');\n		}\n\n		if ($(this).val().includes(\'/\')) {\n			hasError = true;\n			$(\'#sug-error-body\').append(\'<p class=\"text-danger\"><b>{{ sug_redirects_error_slash }}</b></p>\');\n		}\n		\n		if ($(this).val().includes(\'http\')) {\n			hasError = true;\n			$(\'#sug-error-body\').append(\'<p class=\"text-danger\"><b>{{ sug_redirects_error_protocol }}</b></p>\');\n		}\n		\n		if ($(this).val().includes(\'.html\')) {\n			hasError = true;\n			$(\'#sug-error-body\').append(\'<p class=\"text-warning\"><b>{{ sug_redirects_error_html }}</b></p>\');\n		}\n		\n		if (hasError) {\n			$(this).css(\'border-color\', \'red\');\n			\n			$(\'#sug-error-body\').append(\'<p class=\"alert alert-info\"><i class=\"fa fa-info-circle\" style=\"font-size: 1.5em;\">&nbsp;</i>  {{ sug_redirects_error_common }}</p>\');\n			\n			$(\'#sug-error\').modal(\'toggle\');\n		}\n		\n    console.log(\'hasError : \' + hasError);\n    console.log($(this).val());\n		\n  });\n	\n</script>\n\n<!-- Confirm Modal -->\n<div class=\"modal fade\" id=\"sug-confirm\" tabindex=\"-1\" role=\"dialog\" aria-labelledby=\"sug-confirm\" aria-hidden=\"true\">\n	<div class=\"modal-dialog\">\n		<div class=\"modal-content\">\n			<div class=\"modal-header\">\n				<button type=\"button\" class=\"close\" data-dismiss=\"modal\" aria-hidden=\"true\">&times;</button>\n				<h4 class=\"modal-title text-warning\" id=\"sug-confirm-title\"><i class=\"fa fa-exclamation-triangle\">&nbsp;</i> <b>{{ sug_confirm_title }}</b></h4>\n			</div>\n			<div class=\"modal-body\">\n				{{ sug_confirm_body }}\n			</div>\n			<div class=\"modal-footer\">\n				<button type=\"button\" class=\"btn btn-danger sug-btn-confirm\" id=\"sug-btn-confirm\" data-target=\"\"><i class=\"fa fa-trash\">&nbsp;</i> {{ sug_confirm_btn_yes }}</button>\n				<button type=\"button\" class=\"btn btn-default\" data-dismiss=\"modal\">{{ sug_confirm_btn_no }}</button>\n			</div>\n		</div>\n	</div>\n</div>\n\n<!-- Error Modal -->\n<div class=\"modal fade\" id=\"sug-error\" tabindex=\"-1\" role=\"dialog\" aria-labelledby=\"sug-error\" aria-hidden=\"true\">\n	<div class=\"modal-dialog\">\n		<div class=\"modal-content\">\n			<div class=\"modal-header\">\n				<button type=\"button\" class=\"close\" data-dismiss=\"modal\" aria-hidden=\"true\">&times;</button>\n				<h4 class=\"modal-title text-danger\" id=\"sug-error-title\"><i class=\"fa fa-exclamation-triangle\">&nbsp;</i> <b>{{ sug_redirects_error_title }}</b></h4>\n			</div>\n			<div class=\"modal-body\" id=\"sug-error-body\"></div>\n		</div>\n	</div>\n</div>\n{% endif %}\n<!-- /SEO URL Generator . End\n============================================================================ -->\n]]></add>\n		</operation>\n	</file>\n  \n</modification>\n', 1, '2022-01-25 13:48:25');
INSERT INTO `oc_modification` (`modification_id`, `extension_install_id`, `name`, `code`, `author`, `version`, `link`, `xml`, `status`, `date_added`) VALUES
(2, 0, '2 simple url rewrite', '2 simple url rewrite', 'deeman', '2.0.0', 'http://simpleopencart.com', '\r\n            <modification>\r\n                <name>2 simple url rewrite</name>\r\n                <code>2 simple url rewrite</code>\r\n                <version>2.0.0</version>\r\n                <author>deeman</author>\r\n                <link>http://simpleopencart.com</link>\r\n\r\n                <file path=\"catalog/controller/startup/startup.php\">\r\n                    <operation error=\"skip\">\r\n                        <search><![CDATA[$this->registry->set(\'url\']]></search>\r\n                        <add position=\"after\"><![CDATA[$this->url->addRewrite(new Simple\\Rewrite($this->config, $this->session));]]></add>\r\n                    </operation>\r\n                </file>\r\n            </modification>', 0, '2022-01-27 11:39:22'),
(3, 4, 'Advanced News System', 'advanced_news_system', 'Equotix', '4.0.0', 'https://www.equotix.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<modification>\r\n	<name>Advanced News System</name>\r\n	<code>advanced_news_system</code>\r\n	<version>4.0.0</version>\r\n	<author>Equotix</author>\r\n	<link>https://www.equotix.com</link>\r\n	<file path=\"admin/controller/common/column_left.php\">\r\n		<operation>\r\n			<search><![CDATA[\r\n				if ($this->user->hasPermission(\'access\', \'marketplace/event\')) {\r\n			]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n				if ($this->user->hasPermission(\'access\', \'extension/news\')) {\r\n					$marketplace[] = array(\r\n						\'name\'	   => \'News\',\r\n						\'href\'     => $this->url->link(\'extension/news\', \'user_token=\' . $this->session->data[\'user_token\'], true),\r\n						\'children\' => array()		\r\n					);\r\n				}\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/controller/common/footer.php\">\r\n		<operation>\r\n			<search><![CDATA[\r\n				$data[\'contact\'] = $this->url->link(\'information/contact\');\r\n			]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n				$data[\'news\'] = $this->url->link(\'extension/news\');\r\n				$data[\'text_news\'] = $this->language->get(\'text_news\');\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/language/*/common/footer.php\">\r\n		<operation>\r\n			<search index=\"0\"><![CDATA[\r\n				$_\r\n			]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n				$_[\'text_news\'] = \'Our News\';\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/view/theme/*/template/common/footer.twig\">\r\n		<operation>\r\n			<search><![CDATA[\r\n				{{ voucher }}\r\n			]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n				<li><a href=\"{{ news }}\">{{ text_news }}</a></li>\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/controller/startup/seo_url.php\">\r\n		<operation>\r\n			<search><![CDATA[\r\n				if ($url[0] == \'information_id\') {\r\n			]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n				if ($url[0] == \'news_id\') {\r\n					$this->request->get[\'news_id\'] = $url[1];\r\n				}	\r\n			]]></add>\r\n		</operation>\r\n		<operation>\r\n			<search><![CDATA[\r\n				} elseif (isset($this->request->get[\'information_id\'])) {\r\n			]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n				} elseif (isset($this->request->get[\'news_id\'])) {\r\n					$this->request->get[\'route\'] = \'extension/news/news\';\r\n			]]></add>\r\n		</operation>\r\n		<operation>\r\n			<search><![CDATA[\r\n				if (($data[\'route\'] == \'product/product\' && $key == \'product_id\') || (($data[\'route\'] == \'product/manufacturer/info\' || $data[\'route\'] == \'product/product\') && $key == \'manufacturer_id\') || ($data[\'route\'] == \'information/information\' && $key == \'information_id\')) {\r\n			]]></search>\r\n			<add position=\"replace\"><![CDATA[\r\n				if (($data[\'route\'] == \'extension/news/news\' && $key == \'news_id\') || ($data[\'route\'] == \'product/product\' && $key == \'product_id\') || (($data[\'route\'] == \'product/manufacturer/info\' || $data[\'route\'] == \'product/product\') && $key == \'manufacturer_id\') || ($data[\'route\'] == \'information/information\' && $key == \'information_id\')) {\r\n			]]></add>\r\n		</operation>\r\n		<operation>\r\n			<search><![CDATA[\r\n				if ($query->row[\'query\'] && $url[0] != \'information_id\' && $url[0] != \'manufacturer_id\' && $url[0] != \'category_id\' && $url[0] != \'product_id\') {\r\n			]]></search>\r\n			<add position=\"replace\"><![CDATA[\r\n				if ($query->row[\'query\'] && $url[0] != \'information_id\' && $url[0] != \'manufacturer_id\' && $url[0] != \'category_id\' && $url[0] != \'product_id\' && $url[0] != \'news_id\') {\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n</modification>', 1, '2022-01-28 15:54:02'),
(4, 5, 'FAQ Accordion', 'scr_faq', 'https://www.linkedin.com/in/stasionok', '1.0.1', 'https://web-masrshal.ru', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n	<code>scr_faq</code>\n    <name>FAQ Accordion</name>\n    <version>1.0.1</version>\n    <author>https://www.linkedin.com/in/stasionok</author>\n    <link>https://web-masrshal.ru</link>\n</modification>', 1, '2022-02-11 14:36:26'),
(6, 7, 'TO TOP', 'dotboxtotop', '◘ Dotbox Creative', '1.1', 'http://dotboxcreative.com/', '﻿<modification>\r\n		<name>TO TOP</name>\r\n		<code>dotboxtotop</code>\r\n        <version>1.1</version>\r\n        <author>◘ Dotbox Creative</author>\r\n		<link>http://dotboxcreative.com/</link>\r\n		<file path=\"catalog/view/theme/*/template/common/footer.twig\">\r\n				<operation>\r\n                        <search><![CDATA[</html>]]></search>\r\n                        <add position=\"before\"><![CDATA[\r\n						 <style type=\"text/css\">\r\n							#ToTopHover {\r\n							cursor: pointer;\r\n							background: url(data:image/png;base64,) no-repeat left -51px;width: 51px;height: 51px;display: block;overflow: hidden;float: left;opacity: 0;-moz-opacity: 0;filter: alpha(opacity=0);}\r\n							#ToTop {display: none;text-decoration: none;position: fixed;bottom: 20px;right: 20px;overflow: hidden;width: 51px;height: 51px;border: none;text-indent: -999px;background: url(data:image/png;base64,) no-repeat left top;}\r\n						</style>\r\n						<script type=\"text/javascript\">\r\n						/* UItoTop jQuery */\r\n						jQuery(document).ready(function(){$().UItoTop({easingType:\'easeOutQuint\'});});\r\n						(function($){\r\n							$.fn.UItoTop = function(options) {\r\n								var defaults = {\r\n									text: \'To Top\',\r\n									min: 200,\r\n									inDelay:600,\r\n									outDelay:400,\r\n									containerID: \'ToTop\',\r\n									containerHoverID: \'ToTopHover\',\r\n									scrollSpeed: 1600,\r\n									easingType: \'linear\'\r\n								};\r\n								var settings = $.extend(defaults, options);\r\n								var containerIDhash = \'#\' + settings.containerID;\r\n								var containerHoverIDHash = \'#\'+settings.containerHoverID;\r\n								$(\'body\').append(\'<span id=\"\'+settings.containerID+\'\">\'+settings.text+\'</span>\');\r\n								$(containerIDhash).hide().click(function(event){\r\n									$(\'html, body\').animate({scrollTop: 0}, settings.scrollSpeed);\r\n									event.preventDefault();\r\n								})\r\n								.prepend(\'<span id=\"\'+settings.containerHoverID+\'\"></span>\')\r\n								.hover(function() {\r\n										$(containerHoverIDHash, this).stop().animate({\r\n											\'opacity\': 1\r\n										}, 600, \'linear\');\r\n									}, function() { \r\n										$(containerHoverIDHash, this).stop().animate({\r\n											\'opacity\': 0\r\n										}, 700, \'linear\');\r\n									});			\r\n								$(window).scroll(function() {\r\n									var sd = $(window).scrollTop();\r\n									if(typeof document.body.style.maxHeight === \"undefined\") {\r\n										$(containerIDhash).css({\r\n											\'position\': \'absolute\',\r\n											\'top\': $(window).scrollTop() + $(window).height() - 50\r\n										});\r\n									}\r\n									if ( sd > settings.min ) \r\n										$(containerIDhash).fadeIn(settings.inDelay);\r\n									else \r\n										$(containerIDhash).fadeOut(settings.Outdelay);\r\n								});\r\n						};\r\n						})(jQuery);\r\n						</script>\r\n                        ]]></add>\r\n                </operation>\r\n          </file>\r\n</modification>', 1, '2022-03-05 10:58:07');
